//Access Local Filesystem

object LocalIO {

    import java.io.File
    import scala.io.Source

    val IN_DIR_NAME = "/input_files"
    val OUT_DIR_NAME = "/output_files"

    private val _thisDir:String = new File(".").getCanonicalPath
    private var _workingDir:String = _thisDir

    lazy val inputFileDir = _workingDir+IN_DIR_NAME
    lazy val outputFileDir = _workingDir+OUT_DIR_NAME

    val visibleFile = (file:File) => !file.isHidden
    val notDirectory = (file:File) => !file.isDirectory
    val textFile = (file:File) => file.getName.split('.').last.contains("txt")

    def setWorkingDir(directoryPath:String) = {
        println(s"Setting the working directory to: $directoryPath")
        this._workingDir = directoryPath
    }

    def directoryFromString(directory:String):Option[File] = {
         val newDir = new File(directory)
         if (newDir.exists && newDir.isDirectory) Some(newDir)
         else None
    }

    def listFiles(directory:String):List[File] = {
        directoryFromString(directory) match {
            case Some(dir) => dir.listFiles.toList
            case None => List[File]()
        }
    }

    def workingDirFiles = listFiles(_workingDir)

    def readFile(file:File) = {
        val source = Source.fromFile(file.getCanonicalPath)
        try {
            source.getLines.mkString("\n\n")
        } finally {
            source.close
        }
    }
}

//Classes for TAP data
case class Message(message:String)
  //type Results[T] = List[T]
  case class Result[T](message:String,results:T)
  case class Tags(lemmas:List[String] = List(),posTags:List[String] = List())
  case class Sentence(text:String,tokens:List[String],tags:Tags = Tags())
  case class Section(sentences:List[Sentence])
  case class Document(sections:List[Section])

//The TapIO class
case class TapIO(server:String = "http://localhost:8080") {

    //Import some helper libraries
    import scalaj.http._    // to manage http connection to TAP
    import org.json4s._     // to serialise and deserialise JSON
    import org.json4s.jackson.Serialization
    import org.json4s.jackson.Serialization.{read, write}

    implicit val defaultFormats = Serialization.formats(NoTypeHints)

    val API_VERSION = "v1"
    lazy val API_URL = server+"/"+API_VERSION
    lazy val HEALTH_URL = API_URL+"/health"
    lazy val CLEAN_URL = API_URL+"/analyse/text/clean"
    lazy val STRUCTURE_URL = API_URL+"/analyse/text/structure"

    def serverDetails = Http(API_URL).asString

    def getHealthMessage(debug:Boolean = false) = {
        println(s"Connecting to $HEALTH_URL")
        val response = Http(HEALTH_URL).asString
        if(debug) println(response)
        read[Message](response.body)
    }

    def serverIsHealthy = {
        try { getHealthMessage().message=="ok" }
        catch { case e:Exception => {
                println(s"There was a problem with the server: $e")
                false }
        }
    }

    def cleanText(text:String) = {
    //println(s"Cleaning text: $text")
    val response = Http(CLEAN_URL).postData(text).header("content-type", "application/json").asString
    //println(response)
    read[Result[List[String]]](response.body)
  }

  def getStructure(text:String) = {
    //println(s"Getting structure for text: $text")
    val response = Http(STRUCTURE_URL).postData(text).header("content-type", "application/json").asString
    //println(response)
    read[Result[Document]](response.body)
  }
}

val tapServer = TapIO()

def pharmacyText = try {
    //First, try and read in the file
    val inputTextFiles = LocalIO.listFiles(LocalIO.inputFileDir).filter(LocalIO.textFile)
    val text = LocalIO.readFile(inputTextFiles.find(_.getName.contains("pharm")).get)
    //Next, post the text to TAP for cleaning
    val cleanedText = tapServer.cleanText(text).results
    //If successful return the cleaned text
    Some(cleanedText)
} catch {
    case(e:Exception) => {
        println(s"ERROR: There was a problem reading the file. $e")
        None
    }
}

val document = tapServer.getStructure("Hello, this is a test for structure.\n\nThis should be a new section.\n\nAnd this should be the final section.").results

def displayStructure(document:Document) = {
    document.sections.map { section =>
        "section\t\t\t\n".+{
            section.sentences.map { sentence =>
                "\ttext: "+ sentence.text + "\t\t\n"+
                "\ttokens: |"+ sentence.tokens.mkString("|")+"|\t\n"+
                "\t\tlemmas: |"+ sentence.tags.lemmas.mkString("|")+"|\n"+
                "\t\tposTags: |"+ sentence.tags.posTags.mkString("|")+"|\n"
            }.mkString("")
        }
    }.mkString("")
}

val structure = displayStructure(document)

