object LocalIO {

    import java.io.File
    import scala.io.Source

    val IN_DIR_NAME = "/input_files"
    val OUT_DIR_NAME = "/output_files"

    private val _thisDir:String = new File(".").getCanonicalPath
    private var _workingDir:String = _thisDir

    lazy val inputFileDir = _workingDir+IN_DIR_NAME
    lazy val outputFileDir = _workingDir+OUT_DIR_NAME

    val visibleFile = (file:File) => !file.isHidden
    val notDirectory = (file:File) => !file.isDirectory
    val textFile = (file:File) => file.getName.split('.').last.contains("txt")

    def setWorkingDir(directoryPath:String) = {
        println(s"Setting the working directory to: $directoryPath")
        this._workingDir = directoryPath
    }

    def directoryFromString(directory:String):Option[File] = {
         val newDir = new File(directory)
         if (newDir.exists && newDir.isDirectory) Some(newDir)
         else None
    }

    def listFiles(directory:String):List[File] = {
        directoryFromString(directory) match {
            case Some(dir) => dir.listFiles.toList
            case None => List[File]()
        }
    }

    def workingDirFiles = listFiles(_workingDir)

    def readFile(file:File) = {
        val source = Source.fromFile(file.getCanonicalPath)
        try {
            source.getLines.mkString("\n\n")
        } finally {
            source.close
        }
    }
}

