    }
}


def histogram(input:List[Int],bins:Int,min:Option[Int]=None,max:Option[Int]=None): List[Int] = {
  val rangeMin = min.getOrElse(input.min).toDouble
  val binWidth = (max.getOrElse(input.max).toDouble - rangeMin)/bins

  def upper(c:Int) = rangeMin+(binWidth*c)

  def histogramBuilder(inputList:List[Int],count:Int=1,histogram:List[Int]=List()): List[Int] = {
    val lists = inputList.partition(_ <= upper(count))
    val newHist = histogram :+ lists._1.length
    if (newHist.length == bins) newHist
    else histogramBuilder(lists._2,count+1,newHist)
  }

  histogramBuilder(input)
}

def histogramDbl(input:List[Double],bins:Int,min:Option[Double]=None,max:Option[Double]=None): List[Int] = {
